import React from 'react';
import {Route} from 'react-router-dom'
import CollectionPageContainer from '../collection/collection.container'
import {connect} from 'react-redux'
import { fetchCollectionStartAsync } from '../../redux/shop/shop.action';
import CollectionsOverviewContainer from '../../component/collections-overview/collections-overview.container'

class ShopPage extends React.Component{
  componentDidMount(){
    const {ffetchCollectionStartAsync} = this.props;
    ffetchCollectionStartAsync();

  }

  render() {
    const {match} = this.props
    return(
      <div className='shop-page'>
        <Route exact path={`${match.path}`} component={CollectionsOverviewContainer}/>
        <Route path={`${match.path}/:collectionId`} component={CollectionPageContainer} />
      </div>
    )
  }
} 

const mapDispatchToProps = dispatch => ({
  ffetchCollectionStartAsync: () => dispatch(fetchCollectionStartAsync())
})

export default connect(null,mapDispatchToProps)(ShopPage);
