import {createSelector} from 'reselect'

const selectShop = state => state.shop;

export const selectCollections = createSelector(
  [selectShop],
  shop => shop.collections
)

export const selectColletionArray = createSelector(
  [selectCollection],
  collection => Object.keys(collection).map(
    key=> collection[key]
  )
)

export const selectCollection = collectionUrlParameter => {
  createSelector(
    [selectCollection],
    collections => collections.find(collection => collection[collectionUrlParameter])
  )
}
